const formBtn=document.querySelector(".address-button"),modalWindow=document.querySelector(".modal-window"),commentClose=modalWindow.querySelector(".modal-close-comment"),modalForm=modalWindow.querySelector(".modal-form"),commentName=modalWindow.querySelector(".enter-name"),commentMail=modalWindow.querySelector(".enter-mail");formBtn.addEventListener("click",function(e){e.preventDefault(),modalWindow.classList.add("active"),commentName.focus()}),commentClose.addEventListener("click",function(e){e.preventDefault(),modalWindow.classList.remove("active"),modalWindow.classList.remove("error")}),modalForm.addEventListener("submit",function(e){commentName.value&&commentMail.value||(e.preventDefault(),modalWindow.classList.remove("error"),modalWindow.offsetWidth=modalWindow.offsetWidth,modalWindow.classList.add("error"))}),window.addEventListener("keydown",function(e){27===e.keyCode&&modalWindow.classList.contains("active")&&(e.preventDefault(),modalWindow.classList.remove("active"),modalWindow.classList.add("error"))});
